import React, { Component } from "react";
import { connect } from "react-redux";
import { XOA_SINH_VIEN } from "../Redux/constant/quanlySvConstant";

class ItemSinhVien extends Component {
  render() {
    let { sv } = this.props;
    return (
      <tr>
        <td>{sv.id}</td>
        <td>{sv.name}</td>
        <td>{sv.email}</td>
        <td>{sv.phone}</td>
        <td className="flex">
          <button className="btn btn-success mr-1">Sửa</button>
          <button
            onClick={() => [this.props.xoaSinhVien(sv.id)]}
            className="btn btn-danger"
          >
            Xoá
          </button>
        </td>
      </tr>
    );
  }
}

let mapStateToProps = (dispatch) => {
  return {
    xoaSinhVien: (id) => {
      dispatch({
        type: XOA_SINH_VIEN,
        payload: id,
      });
    },
  };
};

export default connect(mapStateToProps)(ItemSinhVien);
