import React, { Component } from "react";
import Header from "./Header";
import FooterLifeCycle from "./FooterLifeCycle";
export default class ContainerParent extends Component {
  state = {
    number: 0,
    like: 0,
    userInfo: {
      name: "alice",
    },
  };
  componentWillMount() {
    // chay 1 lan duy nhat
    console.log("component will mount");
  }
  componentDidMount() {
    // thuong dung de goi API
    // chay 1 lan duy nhat
    console.log("component did mount");
  }
  componentDidUpdate() {
    // dc tu dong goi khi ham render() duoc goi
    console.log("did update - da duoc thay doi");
  }
  componentWillUnmount() {
    console.log("remove");
  }

  shouldComponentUpdate(nextProps, nextState) {
    console.log("next props", nextProps);
    console.log("next State", nextState);
    if (nextState.number == 6) {
      return false;
    }
    return true;
  }

  render() {
    console.log("render");
    return (
      <div className="container p-5 text-center">
        <Header like={this.state.like} dataUser={this.state.userInfo} />
        <button
          className="btn btn-success"
          onClick={() => {
            this.setState({ number: this.state.number + 1 });
          }}
        >
          Tang so luong
        </button>
        <span className="display-3">{this.state.number}</span>
        {/* {this.state.number < 5 && <FooterLifeCycle />} */}
      </div>
    );
  }
}
